// 根据showId去重到新数组newArr
const newArr = [];
arr.forEach(item =&gt; {
  // 过滤，如果有重复项就添加到过滤数组，那么长度就不为0就不会推入新数组。
  // 如果没有重复项长度就为0就推入新数组。
  newArr.filter(m =&gt; m.showId === item.showId).length === 0 &amp;&amp; 
  newArr.push(item);
});

